import store from "../stateManager/store";
import actionTypes from "../stateManager/actionTypes";

class Service {
constructor()
{}

getAllProducts(pageNumber)
{
    fetch("http://localhost:9000/getAllProducts"+pageNumber)
    .then(res => res.json())
    .then(res => 
      { console.log('!!!!!!!!!!!!!!');
        console.log(res);
       store.dispatch({
           type:actionTypes.ADD_PRODUCTS ,
           payload:res
       })
        return res ; 
      })
}

}
export default Service ;
