import React, { Component } from 'react';
import Checkbox from '@material-ui/core/Checkbox';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

class DepartmentFilter extends Component {
    constructor(props) {
        super(props)
        this.state = {
            Departments :[]
        }

    }
    onChange(e)
    {   if (e.target.checked)
        {
            this.setState({
                Departments :this.state.Departments.push(e.target.value)
            })
        }

        fetch('http://localhost:9000/filterDepartments', {
            method: 'POST',
            // We convert the React state to JSON and send it as the POST body
            body: JSON.stringify({Department:this.state.Departments})
          }).then(function(response) {
            console.log(response)
            return response.json();
          });
    }
    render() {
        return (
            <FormControl component="fieldset">

        <FormLabel component="legend">{this.props.name}</FormLabel>
            <FormGroup>
                {this.props.category.map((item) =>
                    <FormControlLabel
                        control={<Checkbox
                        
                            color="primary"
                            inputProps={{ 'aria-label': 'secondary checkbox' }}
                            value = {item.Department_id}
                            onChange={this.onChange.bind(this)}
                        />}
                        label={item.Department_Name}
                    />
                )
                        }
       
        </FormGroup>
        </FormControl>
      
        );
    }
}

export default DepartmentFilter;
