import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

class DepartmentFilter extends Component {
    constructor(props) {
        super(props)
        this.state = {
            Departments :[]
        }

    }
    onChange(e)
    {   if (e.target.checked)
        {   let departments = this.state.Departments ; 
